mapper.select:
select
channelClassify.* 
from dl_ticket_channel_lottery_classify channelClassify 
left join dl_ticket_channel channel on channelClassify.ticket_channel_id=channel.id
where 1=1
and channel.channel_status=0
and channelClassify.status=0
and channelClassify.lottery_classify_id=#{lotteryClassifyId} 
and cast(channelClassify.sale_end_time as SIGNED) <= (#{minMatchStartTimeSeconds} - UNIX_TIMESTAMP(now()))/60
and channelClassify.min_bet_amount <= #{ticketMoney}
and channelClassify.max_bet_amount >= #{ticketMoney}
and channelClassify.matchine_open_time <= DATE_FORMAT(now(),'%H:%i:%S')
and channelClassify.matchine_close_time >= DATE_FORMAT(now(),'%H:%i:%S')
and channelClassify.min_bet_amount_lottery <= #{minBetAmountLottery}
and channelClassify.max_bet_amount_lottery >= #{maxBetAmountLottery}
and channelClassify.exclude_play_type not like #{excludePlayType}
